    void* contextPtr = _clientDataPtr->contextPtr;

    // Unpack the remaining parameters
    le_wifiClient_Event_t event;
    if (!le_pack_UnpackUint32( &_msgBufPtr, &_msgBufSize,
                                               &event ))
    {
        goto error_unpack;
    }

    // Call the registered handler
